#!/usr/bin/env node
const program = require("commander");

program
	.description("Discord Utils package for the command-line.")
	.version("1.0.0")
	.option("--ds [format]", "Gets a neko image")
	.parse(process.argv);

if (program.ds) {
	const DiscordServices = require("../lib/structures/DiscordServices");
	const Nekos = new DiscordServices()

	switch(program.ds.toLowerCase()) {
		case "pat":
			Nekos.pat().then((res) => console.log(res.url));
			break;
		case "hug":
			Nekos.hug().then((res) => console.log(res.url));
			break;
		case "kiss":
			Nekos.kiss().then((res) => console.log(res.url));
			break;
		case "neko":
			Nekos.neko().then((res) => console.log(res.neko));
			break;
		case "why":
			Nekos.why().then((res) => console.log(res.why));
			break;
		case "lewd":
			Nekos.lewd().then((res) => console.log(res.neko));
			break;
		case "lizard":
			Nekos.lizard().then((res) => console.log(res.url));
			break;
		default:
			console.log(`\nerror: unknown option \`${program.ds}\'\n`)
	}
}